INSERT INTO `country_fact` (id,country_id,fact) VALUES(63,10,'The oldest known dinosaur species had been traced back to Argentina and Brazil. Some are 230 million years old! Well-preserved displays of dinosaur bones (fossils) can be found in the Argentine National Historical Museum in Buenos Aires, Argentina.');
INSERT INTO `country_fact` (id,country_id,fact) VALUES(64,10,'Argentina’s national sport is pato, a game played on horseback. It is similar to both polo and basketball. The word pato means “duck” in Spanish, as early versions of the sport used a live duck inside a basket, instead of a ball. ');
INSERT INTO `country_fact` (id,country_id,fact) VALUES(65,10,'The first animated cartoon was created by an Argentine cartoonist named Quirino Cristiani. These movies were released to the public many years ago, in 1917 and 1918.');
